import React, { useState, useEffect } from 'react';
import {
    Table,
    Button,
    Modal,
    Form,
    Input,
    InputNumber,
    message,
    Spin,
    Popconfirm,
    Space,
    Select
} from 'antd';
import {
    getProducts,
    getProductById,
    createProduct,
    updateProduct,
    deleteProduct,
    searchProducts,
    searchProductsByPriceRange
} from '../services/api';

const { Option } = Select;

const ProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [editingProductId, setEditingProductId] = useState(null);
    const [loading, setLoading] = useState(false);
    const [searchParams, setSearchParams] = useState({});
    const [form] = Form.useForm();
    const [searchForm] = Form.useForm();
    const [cities, setCities] = useState(['Moscow', 'Saint Petersburg', 'New York', 'London']);

    useEffect(() => {
        fetchProducts();
    }, [searchParams]);

    const fetchProducts = async () => {
        try {
            setLoading(true);

            // Проверяем, есть ли параметры для диапазона цен
            const hasPriceRange = searchParams.minPrice !== undefined || searchParams.maxPrice !== undefined;

            // Подготовка параметров
            const params = {
                title: searchParams.title || undefined,
                city: searchParams.city || undefined,
                author: searchParams.author || undefined,
                minPrice: searchParams.minPrice !== undefined ?
                    parseFloat(searchParams.minPrice) : undefined,
                maxPrice: searchParams.maxPrice !== undefined ?
                    parseFloat(searchParams.maxPrice) : undefined
            };

            // Удаляем undefined параметры
            const cleanParams = Object.fromEntries(
                Object.entries(params).filter(([_, value]) => value !== undefined)
            );

            let data;
            if (hasPriceRange) {
                // Используем endpoint для диапазона цен
                data = await searchProductsByPriceRange(cleanParams);
            } else {
                // Используем обычный endpoint поиска
                data = await searchProducts(cleanParams);
            }

            setProducts(data || []);
        } catch (error) {
            message.error('Failed to fetch products');
            console.error('Error fetching products:', error);
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (values) => {
        try {
            const priceRange = values.priceRange || {};

            // Валидация диапазона цен
            if (priceRange.min !== undefined && priceRange.max !== undefined) {
                const min = parseFloat(priceRange.min);
                const max = parseFloat(priceRange.max);

                if (min > max) {
                    message.error('Minimum price cannot be greater than maximum price');
                    return;
                }
            }

            setSearchParams({
                title: values.title || undefined,
                city: values.city || undefined,
                author: values.author || undefined,
                minPrice: priceRange.min !== undefined ? parseFloat(priceRange.min) : undefined,
                maxPrice: priceRange.max !== undefined ? parseFloat(priceRange.max) : undefined
            });
        } catch (error) {
            message.error('Invalid search parameters');
            console.error('Search error:', error);
        }
    };

    // Остальные методы остаются без изменений
    const handleCreate = async () => {
        try {
            const values = await form.validateFields();
            await createProduct(values);
            message.success('Product created successfully');
            resetModal();
            await fetchProducts();
        } catch (error) {
            message.error('Error creating product');
            console.error('Error creating product:', error);
        }
    };

    const handleUpdate = async () => {
        try {
            const values = await form.validateFields();
            await updateProduct(editingProductId, values);
            message.success('Product updated successfully');
            resetModal();
            await fetchProducts();
        } catch (error) {
            message.error('Error updating product');
            console.error('Error updating product:', error);
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteProduct(id);
            message.success('Product deleted successfully');
            await fetchProducts();
        } catch (error) {
            message.error('Error deleting product');
            console.error('Error deleting product:', error);
        }
    };

    const handleEdit = async (id) => {
        try {
            setLoading(true);
            const product = await getProductById(id);
            form.setFieldsValue({
                title: product.title,
                description: product.description,
                price: product.price,
                city: product.city,
                author: product.author
            });
            setEditingProductId(id);
            setIsModalVisible(true);
        } catch (error) {
            message.error('Error loading product data');
            console.error('Error loading product:', error);
        } finally {
            setLoading(false);
        }
    };

    const resetModal = () => {
        form.resetFields();
        setEditingProductId(null);
        setIsModalVisible(false);
    };

    const resetSearch = () => {
        searchForm.resetFields();
        setSearchParams({});
    };

    const columns = [
        {
            title: 'ID',
            dataIndex: 'id',
            key: 'id',
            sorter: (a, b) => a.id - b.id,
            width: 80
        },
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
            sorter: (a, b) => a.title.localeCompare(b.title),
            width: 150
        },
        {
            title: 'Description',
            dataIndex: 'description',
            key: 'description',
            ellipsis: true,
            width: 200
        },
        {
            title: 'Price',
            dataIndex: 'price',
            key: 'price',
            render: (price) => `$${price?.toFixed(2) || '0.00'}`,
            sorter: (a, b) => a.price - b.price,
            width: 120
        },
        {
            title: 'City',
            dataIndex: 'city',
            key: 'city',
            filters: cities.map(city => ({ text: city, value: city })),
            onFilter: (value, record) => record.city === value,
            width: 150
        },
        {
            title: 'Author',
            dataIndex: 'author',
            key: 'author',
            sorter: (a, b) => a.author.localeCompare(b.author),
            width: 150
        },
        {
            title: 'Actions',
            key: 'actions',
            fixed: 'right',
            width: 150,
            render: (_, product) => (
                <Space size="small" style={{ whiteSpace: 'nowrap' }}>
                    <Button onClick={() => handleEdit(product.id)} size="small">
                        Edit
                    </Button>
                    <Popconfirm
                        title="Are you sure to delete this product?"
                        onConfirm={() => handleDelete(product.id)}
                        okText="Yes"
                        cancelText="No"
                    >
                        <Button danger size="small">
                            Delete
                        </Button>
                    </Popconfirm>
                </Space>
            ),
        }
    ];

    return (
        <div style={{ padding: 24 }}>
            <Spin spinning={loading}>
                {/* Блок управления (кнопка создания + форма поиска) */}
                <div style={{
                    marginBottom: 16,
                    background: '#fff',
                    padding: 24,
                    borderRadius: 8
                }}>
                    <div style={{
                        display: 'flex',
                        justifyContent: 'space-between',
                        marginBottom: 16
                    }}>
                        <Button
                            type="primary"
                            onClick={() => setIsModalVisible(true)}
                        >
                            Create Product
                        </Button>
                    </div>

                    <Form
                        layout="inline"
                        form={searchForm}
                        onFinish={handleSearch}
                    >
                        <Form.Item name="title" label="Title">
                            <Input placeholder="Search by title" allowClear />
                        </Form.Item>

                        <Form.Item name="author" label="Author">
                            <Input placeholder="Search by author" allowClear />
                        </Form.Item>

                        <Form.Item name="city" label="City">
                            <Select
                                placeholder="Select city"
                                allowClear
                                style={{ width: 150 }}
                            >
                                {cities.map(city => (
                                    <Option key={city} value={city}>{city}</Option>
                                ))}
                            </Select>
                        </Form.Item>

                        <Form.Item label="Price Range" style={{ marginBottom: 0 }}>
                            <Space>
                                <Form.Item
                                    name={['priceRange', 'min']}
                                    noStyle
                                >
                                    <InputNumber
                                        placeholder="Min $"
                                        min={0}
                                        precision={2}
                                        style={{ width: 100 }}
                                    />
                                </Form.Item>
                                <span>-</span>
                                <Form.Item
                                    name={['priceRange', 'max']}
                                    noStyle
                                >
                                    <InputNumber
                                        placeholder="Max $"
                                        min={0}
                                        precision={2}
                                        style={{ width: 100 }}
                                    />
                                </Form.Item>
                            </Space>
                        </Form.Item>

                        <Form.Item>
                            <Button type="primary" htmlType="submit">Search</Button>
                            <Button
                                style={{ marginLeft: 8 }}
                                onClick={resetSearch}
                            >
                                Reset
                            </Button>
                        </Form.Item>
                    </Form>
                </div>

                {/* Блок таблицы */}
                <div style={{
                    background: '#fff',
                    padding: 24,
                    borderRadius: 8,
                    position: 'relative'
                }}>
                    <Table
                        columns={columns}
                        dataSource={products}
                        rowKey="id"
                        loading={loading}
                        scroll={{
                            x: 'max-content',
                            y: 'calc(100vh - 400px)'
                        }}
                        pagination={{
                            pageSize: 10,
                            showSizeChanger: true,
                            pageSizeOptions: ['10', '20', '50', '100'],
                            position: ['bottomRight']
                        }}
                        bordered
                        sticky={{
                            offsetHeader: 0,
                            offsetScroll: 0
                        }}
                    />
                </div>

                {/* Модальное окно */}
                <Modal
                    title={editingProductId ? 'Edit Product' : 'Create Product'}
                    visible={isModalVisible}
                    onOk={editingProductId ? handleUpdate : handleCreate}
                    onCancel={resetModal}
                    okText={editingProductId ? 'Update' : 'Create'}
                    cancelText="Cancel"
                    confirmLoading={loading}
                    width={700}
                    destroyOnClose
                >
                    <Form form={form} layout="vertical">
                        <Form.Item
                            name="title"
                            label="Title"
                            rules={[{ required: true, min: 2, max: 100 }]}
                        >
                            <Input placeholder="Enter product title" />
                        </Form.Item>

                        <Form.Item
                            name="description"
                            label="Description"
                            rules={[{ required: true, min: 10, max: 1000 }]}
                        >
                            <Input.TextArea rows={4} />
                        </Form.Item>

                        <Form.Item
                            name="price"
                            label="Price ($)"
                            rules={[{ required: true, type: 'number', min: 0 }]}
                        >
                            <InputNumber
                                style={{ width: '100%' }}
                                min={0}
                                precision={2}
                            />
                        </Form.Item>

                        <Form.Item
                            name="city"
                            label="City"
                            rules={[{ required: true }]}
                        >
                            <Select
                                placeholder="Select city"
                                showSearch
                            >
                                {cities.map(city => (
                                    <Option key={city} value={city}>{city}</Option>
                                ))}
                            </Select>
                        </Form.Item>

                        <Form.Item
                            name="author"
                            label="Author/Seller"
                            rules={[{ required: true, min: 2 }]}
                        >
                            <Input placeholder="Enter author name" />
                        </Form.Item>
                    </Form>
                </Modal>
            </Spin>
        </div>
    );
};

export default ProductsPage;
