import React, { useState, useEffect } from 'react';
import {
    Table,
    Button,
    Modal,
    Form,
    Input,
    InputNumber,
    message,
    Spin,
    Popconfirm,
    Space
} from 'antd';
import {
    getProducts,
    getProductById,
    createProduct,
    updateProduct,
    deleteProduct,
    searchProducts
} from '../services/api';

const ProductsPage = () => {
    const [products, setProducts] = useState([]);
    const [isModalVisible, setIsModalVisible] = useState(false);
    const [editingProductId, setEditingProductId] = useState(null);
    const [loading, setLoading] = useState(false);
    const [searchParams, setSearchParams] = useState({});
    const [form] = Form.useForm();
    const [searchForm] = Form.useForm();

    useEffect(() => {
        fetchProducts();
    }, [searchParams]);

    const fetchProducts = async () => {
        try {
            setLoading(true);
            let data;

            // Формируем параметры запроса
            const params = {
                title: searchParams.title || undefined,
                city: searchParams.city || undefined,
                author: searchParams.author || undefined,
                minPrice: searchParams.minPrice || undefined,
                maxPrice: searchParams.maxPrice || undefined
            };

            // Если есть параметры поиска - используем searchProducts
            if (Object.values(params).some(param => param !== undefined)) {
                data = await searchProducts(params);
            } else {
                data = await getProducts();
            }

            setProducts(data || []);
        } catch (error) {
            message.error('Failed to fetch products');
        } finally {
            setLoading(false);
        }
    };

    const handleSearch = (values) => {
        setSearchParams({
            title: values.title || undefined,
            city: values.city || undefined,
            author: values.author || undefined,
            minPrice: values.priceRange?.min || undefined,
            maxPrice: values.priceRange?.max || undefined
        });
    };

    // Остальные методы остаются без изменений
    const handleCreate = async () => {
        try {
            const values = await form.validateFields();
            await createProduct(values);
            message.success('Product created successfully');
            resetModal();
            await fetchProducts();
        } catch (error) {
            message.error('Error creating product');
        }
    };

    const handleUpdate = async () => {
        try {
            const values = await form.validateFields();
            await updateProduct(editingProductId, values);
            message.success('Product updated successfully');
            resetModal();
            await fetchProducts();
        } catch (error) {
            message.error('Error updating product');
        }
    };

    const handleDelete = async (id) => {
        try {
            await deleteProduct(id);
            message.success('Product deleted successfully');
            await fetchProducts();
        } catch (error) {
            message.error('Error deleting product');
        }
    };

    const handleEdit = async (id) => {
        try {
            setLoading(true);
            const product = await getProductById(id);
            form.setFieldsValue({
                title: product.title,
                description: product.description,
                price: product.price,
                city: product.city,
                author: product.author
            });
            setEditingProductId(id);
            setIsModalVisible(true);
        } catch (error) {
            message.error('Error loading product data');
        } finally {
            setLoading(false);
        }
    };

    const resetModal = () => {
        form.resetFields();
        setEditingProductId(null);
        setIsModalVisible(false);
    };

    const resetSearch = () => {
        searchForm.resetFields();
        setSearchParams({});
    };

    const columns = [
        {
            title: 'ID',
            dataIndex: 'id',
            key: 'id',
            sorter: (a, b) => a.id - b.id,
        },
        {
            title: 'Title',
            dataIndex: 'title',
            key: 'title',
            sorter: (a, b) => a.title.localeCompare(b.title),
        },
        {
            title: 'Description',
            dataIndex: 'description',
            key: 'description',
            ellipsis: true,
        },
        {
            title: 'Price',
            dataIndex: 'price',
            key: 'price',
            render: (price) => `$${price?.toFixed(2) || '0.00'}`,
            sorter: (a, b) => a.price - b.price,
        },
        {
            title: 'City',
            dataIndex: 'city',
            key: 'city',
            filters: [
                { text: 'Moscow', value: 'Moscow' },
                { text: 'Saint Petersburg', value: 'Saint Petersburg' },
            ],
            onFilter: (value, record) => record.city.includes(value),
        },
        {
            title: 'Author',
            dataIndex: 'author',
            key: 'author',
            sorter: (a, b) => a.author.localeCompare(b.author),
        },
        {
            title: 'Actions',
            key: 'actions',
            render: (_, product) => (
                <Space>
                    <Button onClick={() => handleEdit(product.id)}>Edit</Button>
                    <Popconfirm
                        title="Are you sure to delete this product?"
                        onConfirm={() => handleDelete(product.id)}
                        okText="Yes"
                        cancelText="No"
                    >
                        <Button danger>Delete</Button>
                    </Popconfirm>
                </Space>
            ),
        },
    ];

    return (
        <Spin spinning={loading}>
            <div style={{ marginBottom: 16 }}>
                <Button
                    type="primary"
                    onClick={() => setIsModalVisible(true)}
                    style={{ marginRight: 8 }}
                >
                    Create Product
                </Button>

                <Form
                    layout="inline"
                    form={searchForm}
                    onFinish={handleSearch}
                >
                    <Form.Item name="title" label="Title">
                        <Input placeholder="Search by title" />
                    </Form.Item>

                    <Form.Item name="author" label="Author">
                        <Input placeholder="Search by author" />
                    </Form.Item>

                    <Form.Item name="city" label="City">
                        <Input placeholder="Search by city" />
                    </Form.Item>

                    <Form.Item label="Price Range" style={{ marginBottom: 0 }}>
                        <Space>
                            <Form.Item name={['priceRange', 'min']} noStyle>
                                <InputNumber
                                    placeholder="Min $"
                                    min={0}
                                    style={{ width: 100 }}
                                />
                            </Form.Item>
                            <span>-</span>
                            <Form.Item name={['priceRange', 'max']} noStyle>
                                <InputNumber
                                    placeholder="Max $"
                                    min={0}
                                    style={{ width: 100 }}
                                />
                            </Form.Item>
                        </Space>
                    </Form.Item>

                    <Form.Item>
                        <Button type="primary" htmlType="submit">Search</Button>
                        <Button
                            style={{ marginLeft: 8 }}
                            onClick={resetSearch}
                        >
                            Reset
                        </Button>
                    </Form.Item>
                </Form>
            </div>

            <Table
                columns={columns}
                dataSource={products}
                rowKey="id"
                loading={loading}
                scroll={{ x: true }}
                pagination={{ pageSize: 10 }}
            />

            <Modal
                title={editingProductId ? 'Edit Product' : 'Create Product'}
                visible={isModalVisible}
                onOk={editingProductId ? handleUpdate : handleCreate}
                onCancel={resetModal}
                okText={editingProductId ? 'Update' : 'Create'}
                cancelText="Cancel"
                confirmLoading={loading}
                width={700}
            >
                <Form form={form} layout="vertical">
                    <Form.Item
                        name="title"
                        label="Title"
                        rules={[
                            { required: true, message: 'Please input product title!' },
                            { min: 2, message: 'Title must be at least 2 characters' },
                            { max: 100, message: 'Title cannot exceed 100 characters' }
                        ]}
                    >
                        <Input placeholder="Enter product title" />
                    </Form.Item>

                    <Form.Item
                        name="description"
                        label="Description"
                        rules={[
                            { required: true, message: 'Please input product description!' },
                            { min: 10, message: 'Description must be at least 10 characters' },
                            { max: 1000, message: 'Description cannot exceed 1000 characters' }
                        ]}
                    >
                        <Input.TextArea rows={4} placeholder="Enter detailed description" />
                    </Form.Item>

                    <Form.Item
                        name="price"
                        label="Price ($)"
                        rules={[
                            { required: true, message: 'Please input product price!' },
                            { type: 'number', min: 0, message: 'Price cannot be negative' }
                        ]}
                    >
                        <InputNumber
                            style={{ width: '100%' }}
                            min={0}
                            placeholder="Enter price in dollars"
                        />
                    </Form.Item>

                    <Form.Item
                        name="city"
                        label="City"
                        rules={[
                            { required: true, message: 'Please input city!' }
                        ]}
                    >
                        <Input placeholder="Enter city where product is located" />
                    </Form.Item>

                    <Form.Item
                        name="author"
                        label="Author/Seller"
                        rules={[
                            { required: true, message: 'Please input author/seller name!' }
                        ]}
                    >
                        <Input placeholder="Enter author or seller name" />
                    </Form.Item>
                </Form>
            </Modal>
        </Spin>
    );
};

export default ProductsPage;
